document.onreadystatechange = function(e)
{
  if(document.readyState=="interactive")
  {
    var all = document.getElementsByTagName("*");
    for (var i=0, max=all.length; i < max; i++)
    {
      set_ele(all[i]);
    }
  }
}

function check_element(ele)
{
  var all = document.getElementsByTagName("*");
  var totalele=all.length;
  var per_inc=100/all.length;

  if($(ele).on())
  {
    var prog_width=per_inc+Number(document.getElementById("progress_width").value);
    document.getElementById("progress_width").value=prog_width;
    $("#preloader-bar").css('width',prog_width+"%");
  }
  else
  {
    set_ele(ele);
  }
}

function set_ele(set_element)
{
  check_element(set_element);
}

$(window).on('load', function(){
    $('.preloader').hide();
});

$(document).ready(function(){

    setInterval('check_internet()',5000);

    $(".form-control").on("focus", function(){
        $(this).prev('.input-group-addon').fadeOut(100);
         $(".form-control").css('border-left-radius','4px')
        $(this).next('.input-group-addon').fadeIn(100);
    });

    $(".form-control").on("blur", function(){
        $(this).prev('.input-group-addon').fadeIn(100);
        $(this).next('.input-group-addon').fadeOut(100);
    });

    $(".no-form-control").on("focus", function(){
        $(this).prev('.input-group-addon').fadeIn(100);
         $(".form-control").css('border-left-radius','4px')
        $(this).next('.input-group-addon').fadeIn(100);
    });

    $(".no-form-control").on("blur", function(){
        $(this).prev('.input-group-addon').fadeIn(100);
        $(this).next('.input-group-addon').fadeIn(100);
    });

});
function check_internet(){
    $.ajax({
        url: '/',
        error: function(jqXHR, exception){
            console.log(jqXHR);
            $('.no-internet').fadeIn(500);
        },
        success: function(){
            $('.no-internet').fadeOut(500);
        }
    })
}

